import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:flutter_bloc_login/home/bloc/home_bloc.dart';
import 'package:flutter_bloc_login/login/view/login_page.dart';

class HomePage extends StatefulWidget {
  HomePage({
    super.key,
    required this.homeBloc,
  });

  static String route() => '/home';

  final HomeBloc homeBloc;

  @override
  State<HomePage> createState() => _HomePageState();
}

class _HomePageState extends State<HomePage> {
  HomeBloc get _homeBloc => widget.homeBloc;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: const Text('Home')),
      body: Center(
        child: Column(
          mainAxisSize: MainAxisSize.min,
          children: <Widget>[
            Text('User Logado'),
            BlocBuilder<HomeBloc, HomeState>(
              bloc: _homeBloc,
              buildWhen: (previous, current) => previous.status != current.status,
              builder: (context, state) {
                if (state.status == HomeStatus.loading) {
                  return CircularProgressIndicator();
                } else if (state.status == HomeStatus.success) {
                  return Text(state.textFromImage);
                }
                return ElevatedButton(
                  child: const Text('Teste Imagem'),
                  onPressed: () {
                    _homeBloc.add(HomeImageRecognitionSubmitted(_image64));
                  },
                );
              },
            ),
            Spacer(),
            ElevatedButton(
              child: const Text('Logout'),
              onPressed: () {
                Navigator.pushNamedAndRemoveUntil(context, LoginPage.route(), (route) => false);
              },
            ),
          ],
        ),
      ),
    );
  }

  String _image64 =
      "";
}
